import { useContext } from 'react';
import { Link } from 'react-router-dom';
import Logo from '../../assets/images/logo.jpeg';
import Button from '@mui/material/Button';
import CountryDropdown from './CountryDropdown';
import { IoSearchOutline } from "react-icons/io5";
import { LuUser } from "react-icons/lu";
import { IoBagOutline } from "react-icons/io5";
import SearchBox from './SearchBox';
import Navbar from './Navigation';
import { MyContext } from '../../App';


const Header =() => {
    const context = useContext(MyContext);

    return ( 
        <>
            <div className="headerWrapper">
                <div className="top-strip bg-blue">
                    <div className="container">
                        <p className="mb-0 mt-0 text-center">Due to the <b>COVID 19</b> epidemic, orders may be processed with a slight delay</p>
                    </div>
                </div>
                <header className="header">
                    <div className="container">
                        <div className="row">
                            <div className="logoWrapper d-flex align-items-center col-sm-2">
                                <Link to={'/'}>
                                    <img src={Logo} alt="Logo" />
                                </Link>
                            </div>
                            <div className="col-sm-10 d-flex align-items-center part2">
                            <CountryDropdown />
                            <SearchBox />
                            
                            <div className="part3 d-flex align-items-center ml-auto">
                                <Button className='circle mr-3'><LuUser /></Button>
                                <div className="ml-auto cartTab d-flex align-items-center">
                                    <span className='price'>$3.26</span>
                                    <div className="position-relative ml-2 res-hide">
                                        <Button className="circle ml-2"><IoBagOutline /></Button>
                                        <span className='count d-flex align-items-center justify-content-center'>1</span>
                                    </div>
                                </div>
                            </div>
                            </div>
                        </div>
                    </div>
                </header>
                <Navbar />
            </div>
        </>
    );
}

export default Header;
